DROP TABLE IF EXISTS user;
DROP TABLE IF EXISTS location;
DROP TABLE IF EXISTS drug_availability;
DROP TABLE IF EXISTS drug;
DROP TABLE IF EXISTS customer;
DROP TABLE IF EXISTS address;
DROP TABLE IF EXISTS sales_item;
DROP TABLE IF EXISTS sales;

CREATE TABLE location
(
    location_id integer primary key,
    location_name varchar(50),
    location_code varchar(50)
);

CREATE TABLE user
(
    user_id integer primary key auto_increment,
    name varchar(50),
    gender varchar(50),
    contact varchar(50),
    email varchar(50) not null,
    password varchar(50),
    role varchar(50),
    status varchar(50),
    location_id integer,
    foreign key (location_id) references location (location_id)
);

CREATE TABLE drug
(
    drug_id	VARCHAR(32) primary key ,
    drug_name	VARCHAR(512),
    health_condition	VARCHAR(512),
    id_check	VARCHAR(8),
    price Double(40,2)
);

CREATE TABLE drug_availability
(
    availability_id integer primary key auto_increment,
    drug_id	VARCHAR(32),
    location_id	integer,
    availability	VARCHAR(8),
    quantity integer,
    expiry_date DATE
);

CREATE TABLE address
(
    address_id	integer primary key,
    house_number	integer,
    street_name	VARCHAR(512),
    post_code	VARCHAR(32),
    city	VARCHAR(512),
    county	VARCHAR(512),
    country 	VARCHAR(512)
);

CREATE TABLE customer
(
    nhs_number	integer primary key ,
    date_of_birth	DATE,
    last_name	VARCHAR(512),
    first_name	VARCHAR(512),
    gender	VARCHAR(32),
    is_vulnerable	VARCHAR(8),
    address_id	integer,
    location_id	integer,
    allergitic_drug	VARCHAR(512),
    medical_condition	VARCHAR(512),
    foreign key (location_id) references location(location_id),
    foreign key (address_id) references address(address_id)
);

CREATE TABLE sales
(
    sales_id integer primary key,
    location_id	integer,
    customer_nhs integer,
    employee_id	integer,
    id_verified	VARCHAR(8),
    sales_date	DATE,
    foreign key (location_id) references location(location_id),
    foreign key (customer_nhs) references customer(nhs_number),
    foreign key (employee_id) references user(user_id)
);

CREATE TABLE sales_item
(
    sale_item_id integer primary key,
    sales_id	integer,
    drug_id	VARCHAR(32),
    quantity integer,
    price	Double(40,2),
    foreign key (sales_id) references sales(sales_id)
);

CREATE TABLE existing_prescription
(
    prescription_id	integer primary key,
    customer_nhs_number	integer,
    drug_id	VARCHAR(32),
    quantity integer,
    location_id	integer,
    foreign key (customer_nhs_number) references customer(nhs_number),
    foreign key (drug_id) references drug(drug_id),
    foreign key (location_id) references location(location_id)
);

